const puppeteer = require('puppeteer');

(async () => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();
  
  // Navigate to the webpage
  await page.goto('https://www.stlouiswholesale.com/ecommerce/nfl/wholesale-arizona-cardinals.html');

  // Extract data
  const data = await page.evaluate(() => {
    let items = [];
    document.querySelectorAll('.item-container').forEach(item => {
      let image = item.querySelector('img').src;
      let description = item.querySelector('.product-name').textContent.trim();
      let price = item.querySelector('.price').textContent.trim();
      items.push({ image, description, price });
    });
    return items;
  });

  console.log(data);

  await browser.close();
})();
